mapper.update:
	
		
		update board 
			set hit = hit + 1 
		where no = #{no }

mapper.select:
		
		select g_no as gNo, o_no as oNo, depth
		  from board 
		 where no = #{no }

mapper.delete:
		
		update board 
			set hit = -1 
		where no = #{no }

mapper.update:
	
		
		update board 
			set title = #{title }, contents = #{contents } 
		where no = #{no }
